#include <iostream>
#include "SortTestHelper.h"
#include "InsertionSort.h"

using namespace std;


// å°†arr[l...mid]å’Œarr[mid+1...r]ä¸¤éƒ¨åˆ†è¿›è¡Œå½’å¹¶
template<typename  T>
void __merge(T arr[], int l, int mid, int r){

    //* VSä¸æ”¯æŒåŠ¨æ€é•¿åº¦æ•°ç»„, å³ä¸èƒ½ä½¿ç”¨ T aux[r-l+1]çš„æ–¹å¼ç”³è¯·auxçš„ç©ºé—´
    //* ä½¿ç”¨VSçš„åŒå­¦, è¯·ä½¿ç”¨newçš„æ–¹å¼ç”³è¯·auxç©ºé—´
    //* ä½¿ç”¨newç”³è¯·ç©ºé—´, ä¸è¦å¿˜äº†åœ¨__mergeå‡½æ•°çš„æœ€å, deleteæ‰ç”³è¯·çš„ç©ºé—´:)

    //è¾…åŠ©ç©ºé—´å¯¹å¤§å°ï¼Œç”±äºæ˜¯å‰é—­åé—­åŒºé—´ï¼Œæ‰€ä»¥æ˜¯r-l+1
    T aux[r-l+1];
    //T *aux = new T[r-l+1];

    //ä¸´æ—¶ç©ºé—´åˆå§‹åŒ–
    for( int i = l ; i <= r; i ++ )
        //auxæ˜¯ä»é›¶å¼€å§‹çš„ï¼Œä½†æ˜¯å¤„ç†çš„arrç©ºé—´æ˜¯åŠ¨æ€çš„lå¼€å§‹çš„ï¼Œä¸¤ä¸ªæ•°ç»„æœ‰ä¸€ä¸ªlçš„è·ç¦»åç§»
        aux[i-l] = arr[i];

    // åˆå§‹åŒ–ï¼ŒiæŒ‡å‘å·¦åŠéƒ¨åˆ†çš„èµ·å§‹ç´¢å¼•ä½ç½®lï¼›jæŒ‡å‘å³åŠéƒ¨åˆ†èµ·å§‹ç´¢å¼•ä½ç½®mid+1
    int i = l, j = mid+1;
    //ä»léå†åˆ°rï¼Œå·¦è¾¹éå†åˆ°å³è¾¹ï¼Œå†³å®škçš„ä½ç½®
    for( int k = l ; k <= r; k ++ ){

        if( i > mid ){  // å¦‚æœå·¦åŠéƒ¨åˆ†å…ƒç´ å·²ç»å…¨éƒ¨å¤„ç†å®Œæ¯•
            arr[k] = aux[j-l]; j ++;
        }
        else if( j > r ){  // å¦‚æœå³åŠéƒ¨åˆ†å…ƒç´ å·²ç»å…¨éƒ¨å¤„ç†å®Œæ¯•
            arr[k] = aux[i-l]; i ++;
        }
        else if( aux[i-l] < aux[j-l] ) {  // å·¦åŠéƒ¨åˆ†æ‰€æŒ‡å…ƒç´  < å³åŠéƒ¨åˆ†æ‰€æŒ‡å…ƒç´ 
            arr[k] = aux[i-l]; i ++;
        }
        else{  // å·¦åŠéƒ¨åˆ†æ‰€æŒ‡å…ƒç´  >= å³åŠéƒ¨åˆ†æ‰€æŒ‡å…ƒç´ 
            arr[k] = aux[j-l]; j ++;
        }
    }

    //delete[] aux;
}

// é€’å½’ä½¿ç”¨å½’å¹¶æ’åº,å¯¹arr[l...r]çš„èŒƒå›´è¿›è¡Œæ’åº
template<typename T>
void __mergeSort(T arr[], int l, int r){

    //å¤„ç†é€’å½’åˆ°åº•çš„æƒ…å†µï¼Œl>=rè¡¨ç¤ºè¦å¤„ç†çš„æ˜¯è‡³å¤šä¸€ä¸ªå…ƒç´ çš„æƒ…å†µ
    if( l >= r )
        return;

    //è®¡ç®—ä¸­ç‚¹ä½ç½® æ€è€ƒï¼šå½“lå’Œrå¾ˆå¤§æ—¶ï¼Œl+rå¯èƒ½ä¼šæº¢å‡ºï¼Œå¦‚ä½•é¿å…
    int mid = (l+r)/2;
    //å¯¹ä¸€åŠè¿›è¡Œå½’å¹¶æ’åº
    __mergeSort(arr, l, mid);
    //å¯¹å¦ä¸€åŠè¿›è¡Œå½’å¹¶æ’åº
    __mergeSort(arr, mid+1, r);
    //åˆå¹¶ä¸Šé¢å¯¹ä¸¤åŠæ•°æ®
    __merge(arr, l, mid, r);
}

template<typename T>
void mergeSort(T arr[], int n){

    __mergeSort( arr , 0 , n-1 );
}


// æ¯”è¾ƒInsertionSortå’ŒMergeSortä¸¤ç§æ’åºç®—æ³•çš„æ€§èƒ½æ•ˆç‡
// æ•´ä½“è€Œè¨€, MergeSortçš„æ€§èƒ½æœ€ä¼˜, å¯¹äºè¿‘ä¹æœ‰åºçš„æ•°ç»„çš„ç‰¹æ®Šæƒ…å†µ, è§æµ‹è¯•2çš„è¯¦ç»†æ³¨é‡Š
int main() {

    // Merge Sortæ˜¯æˆ‘ä»¬å­¦ä¹ çš„ç¬¬ä¸€ä¸ªO(nlogn)å¤æ‚åº¦çš„ç®—æ³•
    // å¯ä»¥åœ¨1ç§’ä¹‹å†…è½»æ¾å¤„ç†100ä¸‡æ•°é‡çº§çš„æ•°æ®
    // æ³¨æ„ï¼šä¸è¦è½»æ˜“å°è¯•ä½¿ç”¨SelectionSort, InsertionSortæˆ–è€…BubbleSortå¤„ç†100ä¸‡çº§çš„æ•°æ®
    // å¦åˆ™ï¼Œä½ å°±è§è¯†äº†O(n^2)çš„ç®—æ³•å’ŒO(nlogn)ç®—æ³•çš„æœ¬è´¨å·®å¼‚ï¼šï¼‰
    int n = 50000;

    // æµ‹è¯•1 ä¸€èˆ¬æ€§æµ‹è¯•
    cout<<"Test for random array, size = "<<n<<", random range [0, "<<n<<"]"<<endl;
    int* arr1 = SortTestHelper::generateRandomArray(n,0,n);
    int* arr2 = SortTestHelper::copyIntArray(arr1, n);

    SortTestHelper::testSort("Insertion Sort", insertionSort, arr1, n);
    SortTestHelper::testSort("Merge Sort",     mergeSort,     arr2, n);

    delete[] arr1;
    delete[] arr2;

    cout<<endl;


    // æµ‹è¯•2 æµ‹è¯•è¿‘ä¹æœ‰åºçš„æ•°ç»„
    // å¯¹äºè¿‘ä¹æœ‰åºçš„æ•°ç»„, æ•°ç»„è¶Šæœ‰åº, InsertionSortçš„æ—¶é—´æ€§èƒ½è¶Šè¶‹è¿‘äºO(n)
    // æ‰€ä»¥å¯ä»¥å°è¯•, å½“swapTimesæ¯”è¾ƒå¤§æ—¶, MergeSortæ›´å¿«
    // ä½†æ˜¯å½“swapTimeså°åˆ°ä¸€å®šç¨‹åº¦, InsertionSortå˜å¾—æ¯”MergeSortå¿«
    int swapTimes = 10;
    assert( swapTimes >= 0 );

    cout<<"Test for nearly ordered array, size = "<<n<<", swap time = "<<swapTimes<<endl;
    arr1 = SortTestHelper::generateNearlyOrderedArray(n,swapTimes);
    arr2 = SortTestHelper::copyIntArray(arr1, n);

    SortTestHelper::testSort("Insertion Sort", insertionSort, arr1, n);
    SortTestHelper::testSort("Merge Sort",     mergeSort,     arr2, n);

    delete[] arr1;
    delete[] arr2;

    return 0;
}
```

